import { useEffect, useState } from 'react';
import './App.css';
import logo from './logo.svg';

function App() {
  const message = 'Lorem ipsum dolor sit amet, consectetur adipiscing elit. Mauris bibendum orci quam, non mollis erat aliquam a. Quisque a convallis libero. Nam eget neque ante.';
  const [displayText, setDisplayText] = useState('');
  const [index, setIndex] = useState(0);
  const [deleting, setDeleting] = useState(false);
  const [font, setFont] = useState('georgia');

  useEffect(() => {
    const delay = deleting ? 40 : 70;

    const timeout = setTimeout(() => {
      if (!deleting) {
        setDisplayText(message.slice(0, index + 1));
        setIndex(index + 1);

        if (index + 1 === message.length) {
          setTimeout(() => setDeleting(true), 1000);
        }
      } else {
        setDisplayText(message.slice(0, index - 1));
        setIndex(index - 1);

        if (index - 1 === 0) {
          setDeleting(false);
          setFont(font === 'georgia' ? 'montserrat' : 'georgia');
        }
      }
    }, delay);

    return () => clearTimeout(timeout);
  }, [index, deleting]);

  return (
    <div className="App">
      <header className="App-header">
        <img src={logo} alt="Logo" className="logo" />
        <nav className="nav">
          <a href="#">Font Types</a>
          <a href="#">Parts of a Letter</a>
          <button className="learn-btn">Learn</button>
          <button className="quiz-btn">Quiz Yourself</button>
        </nav>
      </header>

      <section className="hero">
        <h1>Anatomy of a Font</h1>
        <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit. 
          Mauris bibendum orci quam, non mollis erat aliquam a. 
          Quisque a convallis libero. Nam eget neque ante.
        </p>
      </section>

      {/* üîÅ Optional Typing Section */}
      <section className="font-demo">
        <h2>Font Style Animation</h2>
        <p className={`typing-text ${font}`}>{displayText}</p>
      </section>
    </div>
  );
}

export default App;
